sys_ui_action.script:
function onSubmitConsultant() {
	var daysInWeek = ['mon', 'tue', 'wed', 'thu', 'fri', 'sat', 'sun'];
	var holidays = g_form.getValue('holidays');
	holidays += ',sat,sun';

	if (holidays)
		holidays = holidays.split(',');

	//This will be an array of true/false values
	//if mon is holiday, holidayArray[0] = true and so on
	var holidayArray = [];
	for (var w in daysInWeek) {
		holidayArray[w] = false;
		for (var h in holidays) {
			if (daysInWeek[w] == holidays[h]) {
				holidayArray[w] = true;
				break;
			}
		}
	}
	// Check and confirm for any days with zero hours
	// If it's a company paid holiday, then check for non-zero hours
	var question;
	for(var d=0; d<daysInWeek.length; d++) {//} in daysInWeek) {
		question = '';
		if (holidayArray[d]) {
			if (g_form.getValue(daysInWeek[d]+'_reg') !=0)
				question = 'Are you sure you want to put hours on a company holiday / weekend for ' + daysInWeek[d] + '?';
		} else if(g_form.getValue(daysInWeek[d]+'_reg') == 0) {
			question = 'Are you sure you want to put 0 hour on ' + daysInWeek[d] + '?';
		}
		if(question != '') {
			var answer = confirm(question);
			if(answer != true) {
				return;
			}
		}
	}
	gsftSubmit(null, g_form.getFormElement(), 'on_submit_consultant');
}

if(typeof window == 'undefined') {
		submitForApprovalConsultant();	
}

function submitForApprovalConsultant() {
	current.state = 'Submitted';
	current.update();
	action.setRedirectURL(current);
	//return false;
}
